  method_ <- match.arg(
    arg = .method,
    choices = c(
      "osa", "lv", "dl", "hamming", "lcs", "qgram", "cosine",
      "jaccard", "jw", "soundex"
    )
  )
  
  tab_ <- stringdist::stringsimmatrix(
    a = .source[[.col]],
    b = .target[[.col]],
    method = method_
