    suppressWarnings()
  tab_[["id_s"]] <- .source[["id"]][tab_[["id_s"]]]
  tab_[["id_t"]] <- .target[["id"]][tab_[["id_t"]]]
  colnames(tab_) <- c("id_s", "id_t", paste0("sim_", .col))
  return(tab_)
}
